    @Test
    public void vcManager() throws Exception {
//        assertArrayEquals(a,b) : 배열 a와b가 일치함을 확인
//        assertEquals(a,b) : 객체 a와b의 값이 같은지 확인
//        assertSame(a,b) : 객체 a와b가 같은 객체임을 확인
//        assertTrue(a) : a가 참인지 확인
//        assertNotNull(a) : a객체가 null이 아님을 확인

        Context appContext = InstrumentationRegistry.getInstrumentation().getTargetContext();
//        assertEquals("com.opendid.walletcore.test", appContext.getPackageName());


        KeyManager<DetailKeyInfo> keyManager = new KeyManager<>("vcTest", appContext);
        if(keyManager.isAnyKeySaved())
            keyManager.deleteAllKeys();
        WalletKeyGenRequest keyGenInfo = new WalletKeyGenRequest();

        if(!keyManager.isKeySaved("FREE")) {
            keyGenInfo = new WalletKeyGenRequest(
                    "FREE",
                    AlgorithmType.ALGORITHM_TYPE.SECP256R1,
                    StorageOption.STORAGE_OPTION.WALLET,
                    new KeyGenWalletMethodType()
            );
            keyManager.generateKey(keyGenInfo);
        }

        // pin Key 생성
        if(!keyManager.isKeySaved("PIN")) {
            keyGenInfo = new WalletKeyGenRequest(
                    "PIN",
                    AlgorithmType.ALGORITHM_TYPE.SECP256R1,
                    StorageOption.STORAGE_OPTION.WALLET,
                    new KeyGenWalletMethodType(MultibaseUtils.encode(MultibaseType.MULTIBASE_TYPE.BASE_64, "111111".getBytes()))
            );
            keyManager.generateKey(keyGenInfo);
        }

        VCManager<VerifiableCredential> vcManager = new VCManager<>("vcTest", appContext);
        if(vcManager.isAnyCredentialsSaved())
            vcManager.deleteAllCredentials();
        // 3.addCredentials
        Log.d("VCManagerTest", "3.addCredentials");
        vcManager.addCredentials(MessageUtil.deserialize(vc2, VerifiableCredential.class));
        vcManager.addCredentials(MessageUtil.deserialize(vc3, VerifiableCredential.class));
        vcManager.addCredentials(MessageUtil.deserialize(vc4, VerifiableCredential.class));

        Log.d("VCManagerTest", "4.getAllCredentials");
        List<VerifiableCredential> vcList = vcManager.getAllCredentials();
        Log.d("VCManagerTest", "getAllCredentials vcList size : " + vcList.size());
        for(VerifiableCredential vc : vcList){
            Log.d("VCManagerTest", "getAllCredentials vc : " + vc.toJson());
        }
        Log.d("VCManagerTest", "5.getCredentials");
        vcList = vcManager.getCredentials(List.of("89999999-9999-9999-9999-999999999999","79999999-9999-9999-9999-999999999999"));
        Log.d("VCManagerTest", "getCredentials vcList size : " + vcList.size());
        for(VerifiableCredential vc : vcList){
            Log.d("VCManagerTest", "getCredentials vc : " + vc.toJson());
        }
        Log.d("VCManagerTest", "6.deleteCredentials");
        vcManager.deleteCredentials(List.of("89999999-9999-9999-9999-999999999999","79999999-9999-9999-9999-999999999999"));
        vcList = vcManager.getAllCredentials();
        Log.d("VCManagerTest", "deleteCredentials vcList size : " + vcList.size());
        for(VerifiableCredential vc : vcList){
            Log.d("VCManagerTest", "deleteCredentials vc : " + vc.toJson());
        }

        Log.d("VCManagerTest", "6.deleteCredentials : vc 남은 1개 삭제");
        vcManager.deleteCredentials(List.of("69999999-9999-9999-9999-999999999999"));

        Log.d("VCManagerTest", "VC 3개 추가");
        vcManager.addCredentials(MessageUtil.deserialize(vc2, VerifiableCredential.class));
        vcManager.addCredentials(MessageUtil.deserialize(vc3, VerifiableCredential.class));
        vcManager.addCredentials(MessageUtil.deserialize(vc4, VerifiableCredential.class));

        //8. vp 생성
        Log.d("VCManagerTest", "8.makeVP");
        List<ClaimInfo> claimInfos = new ArrayList<>();
        ClaimInfo claimInfo = new ClaimInfo(
                "69999999-9999-9999-9999-999999999999",
                List.of("org.iso.18013.5.family_name", "org.iso.18013.5.birth_date")
        );
        claimInfos.add(claimInfo);
        claimInfo = new ClaimInfo();
        claimInfo.setCredentialId("79999999-9999-9999-9999-999999999999");
        claimInfo.setClaimCodes(List.of("org.iso.18013.5.age_in_years"));
        claimInfos.add(claimInfo);
        PresentationInfo presentationInfo = new PresentationInfo();
        presentationInfo.setHolder(DIDManager.genDID("omn")); //holder did
        presentationInfo.setValidFrom("2024-08-15T12:20:36Z");
        presentationInfo.setValidUntil("2030-01-15T12:20:36Z");
        presentationInfo.setVerifierNonce(MultibaseUtils.encode(MultibaseType.MULTIBASE_TYPE.BASE_58_BTC, CryptoUtils.generateNonce(20)));
        VerifiablePresentation vp = vcManager.makePresentation(claimInfos, presentationInfo);
        Log.d("VCManagerTest", " make VP : " + vp.toJson());


        Log.d("VCManagerTest", "7.removeAllCredentials");
        vcManager.deleteAllCredentials();
        Log.d("VCManagerTest", "isAnyCredentialsSaved: " + vcManager.isAnyCredentialsSaved());

    }
